
public class Ascii {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'T';
        
		int asciicode = ch;
		
		//type casting
		 int asciivalue = (int)ch;
		
		System.out.println("The value of T is " +asciicode);
		System.out.println("This is typecasting example " +asciivalue);
	}

}
