
public class CalculateSum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		int num = 10, count,total=0;
		
		for(count=1; count<=num; count++) {
			
			total = count-1;
			
			//System.out.println("The total of num is: "+total);
			
			
		}
		System.out.println("The total of num is: "+total);
		
	}
	
	
}
